\draw[black,thick,->,>=stealth'] (0,-5)--(0,5.8) node[above]{$y$};
\foreach \x  in {-5, 5} {
  \draw[black,thick] (\x,.15)--++(0,-.3) node[below, fill=white, inner sep=1, yshift=-2]{$\x$};
  \draw[black,thick] (.15,\x)--++(-.3,0) node[left, fill=white, inner sep=1, xshift=-2]{$\x$};
}
