\end{tikzpicture}

webwork.pg-code:
$x1 = Compute("1");
$y1 = Compute("5");

sub mychecker {
   my $x = shift;
   return sub {
   my ($correct,$student,$ansHash) = @_;  
   return ($student < (6+3*$x) ? 1 : 0);  
 };
 }

$my_evaluator = $y1 -> cmp(checker=>
mychecker($x1) );

$x0 = Compute("0");
$y0 = Compute("0");
$my_eval2 = $y0 -> cmp(checker=>
mychecker($x0) );

$xn2 = Compute("-2");
$yn2 = Compute("-3");
$my_eval3 = $yn2 -> cmp(checker=>
mychecker($xn2) );

